select
  id, user_id, contract_id, post_id, bpm_process_instance_ext_id, bpm_process_definition_id, creator, create_time, updater, update_time, deleted, tenant_id, process_instance_id
from c_perform_report


        and id = #{id}


        and user_id = #{userId}


        and contract_id = #{contractId}


        and post_id = #{postId}


        and bpm_process_instance_ext_id = #{bpmProcessInstanceExtId}


        and bpm_process_definition_id = #{bpmProcessDefinitionId}


        and creator = #{creator}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and deleted = #{deleted}


        and tenant_id = #{tenantId}


        and process_instance_id = #{processInstanceId}


limit #{pageable.offset}, #{pageable.pageSize}

